import React, { useEffect, useState } from "react";
import { getWatchedMovies, getRatings } from "../services/api";
import Cookies from "js-cookie";
import { Card } from "../components/Card";

export const Watched = () => {
  const [movies, setMovies] = useState([]);
  const [ratings, setRatings] = useState([]);

  const fetchWatched = async () => {
    const data = await getWatchedMovies(Cookies.get("userId"));
    setMovies(data);
  };

  const fetchRatings = async () => {
    const ratingsData = await getRatings(Cookies.get("userId"));
    setRatings(ratingsData);
    
  };

  const ratingMap = {};
  ratings.forEach((rating) => {
    ratingMap[rating.movie_id] = rating.rating;
  });

  useEffect(() => {
    fetchRatings().then(() => {
      fetchWatched();
    })
  }, []);

  return (
    <div className="watchlist-container">
      {movies.map((movie) => (
        <Card
          key={movie.movie_id}
          {...movie}
          rating={ratingMap[movie.movie_id]}
          watched={true}
        ></Card>
      ))}
    </div>
  );
};
