import React, { useEffect, useState } from "react";
import { getWatchedMovies } from "../services/api";
import Cookies from "js-cookie";
import { Card } from "../components/Card";

export const Watched = () => {
  const [movies, setMovies] = useState([]);
  const [ratings, setRatings] = useState([]);

  const fetchWatched = async () => {
    const data = await getWatchedMovies(Cookies.get("userId"));
    setMovies(data);
  };

  const fetchRatings = async () => {
    const ratingsData = await getWatchedMovies(Cookies.get("userId"));
    setRatings(ratingsData);
  };

  const ratingMap = {};
  ratings.forEach((rating) => {
    ratingMap[rating.movie_id] = rating.rating;
  });

  // Map movies to include ratings
  const moviesWithRatings = movies.map((movie) => {
    const movieId = movie.id;
    const rating = ratingMap[movieId]; // Get rating based on movie ID
    return { ...movie, rating }; // Include rating in the movie object
  });

  useEffect(() => {
    fetchWatched();
    fetchRatings();
  }, []);

  return (
    <div className="watchlist-container">
      {movies.map((movie) => (
        <Card key={movie.movie_id} {...movie} watched={true}></Card>
      ))}
    </div>
  );
};
