import React, { useState, useEffect } from 'react';
import './MovieList.css'; // Import CSS file for styling
import MovieCard from './MovieCard'; // Import MovieCard component

const MovieList = ({ movies }) => {
  const [filteredMovies, setFilteredMovies] = useState(movies);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 10;

  // Update filteredMovies whenever movies prop changes
  useEffect(() => {
    setFilteredMovies(movies);
  }, [movies]);

  // Calculate total number of pages based on filteredMovies and itemsPerPage
  const totalPages = Math.ceil(filteredMovies.length / itemsPerPage);

  // Calculate the index range for the current page
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentMovies = filteredMovies.slice(startIndex, endIndex);

  return (
    <div className="movie-list-container">
      {currentMovies.length === 0 ? (
        <div className="no-movies-message">No movies found matching your search criteria.</div>
      ) : (
        <>
          {currentMovies.map((movie) => (
            <MovieCard key={movie.id} {...movie} />
          ))}

          {/* Pagination Controls */}
          <div className="pagination">
            <button
              onClick={() => setCurrentPage((prevPage) => Math.max(prevPage - 1, 1))}
              disabled={currentPage === 1}
            >
              Previous
            </button>
            <span>Page {currentPage} of {totalPages}</span>
            <button
              onClick={() => setCurrentPage((prevPage) => Math.min(prevPage + 1, totalPages))}
              disabled={currentPage === totalPages}
            >
              Next
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default MovieList;
