import React, { useState } from "react";
import { BiSolidAddToQueue } from "react-icons/bi";
import { addToWatchlist } from "../services/api";
import Cookies from "js-cookie";
import { AiFillStar } from "react-icons/ai";
import { rateMovie } from "../services/api";

export const Card = ({ movie_id, title, genre, director, plot, poster }) => {
  const [rating, setRating] = useState(null);

  const handleAddToWatchlist = async () => {
    try{
      const userId = Cookies.get("userId");
      await addToWatchlist(userId, movie_id)
    }
    catch(err) {
      console.log("cant add to watchlist", err);
    }
  }

  const handleRateMovie = async (stars) => {
    try {
      const userId = Cookies.get("userId");
      setRating
      await rateMovie(userId, movie_id, stars);
      console.log("Movie rated successfully!");
    } catch (err) {
      console.error("Failed to rate movie", err);
    }
  };

  return (
    <div className="card">
      <img src={poster} alt={title} className="poster" />
      <div className="details">
        <h2>{title}</h2>
        <p>
          <strong>Genre:</strong> {genre}
        </p>
        <p>
          <strong>Director:</strong> {director}
        </p>
        <p>
          <strong>Plot:</strong> {plot}
        </p>
        {/* Display user's rating */}
        <div className="rating">
          <strong>Rating:</strong>{" "}
          {rating !== null ? (
            Array.from({ length: rating }, (_, index) => (
              <AiFillStar key={index} color="#FFD700" />
            ))
          ) : (
            "Not rated yet"
          )}
        </div>
      </div>
      <div className="card-action">
        <BiSolidAddToQueue
          onClick={handleAddToWatchlist}
          style={{ cursor: "pointer" }}
          size={"30"}
        ></BiSolidAddToQueue>
        {/* Display stars for rating */}
        {[1, 2, 3, 4, 5].map((star) => (
          <AiFillStar
            key={star}
            style={{ cursor: "pointer" }}
            size={25}
            color={rating >= star ? "#FFD700" : "#ccc"}
            onClick={() => handleRateMovie(star)}
          />
        ))}
      </div>
    </div>
  );
};
